import React, { Component } from "react";
import {
  Button,
} from "@material-ui/core";
import Dropzone, {useDropzone} from 'react-dropzone';
import firebase from '../../../firebase'

export class FormPersonalDetails extends Component {
  continue = e => {
    e.preventDefault();
    this.props.nextStep();
  };
  back = e => {
    e.preventDefault();
    this.props.prevStep();
  };
  constructor() {
    super();
    this.onDrop = (files) => {
        this.getAsText(files[0]);
        this.props.handleChangefile(files)
    }
    this.state = {
      disabled: false,
      files: [],
      downloadURL: null
    };
  }
  


  getAsText=(fileToRead)=> {
    var reader = new FileReader();
    // Read file into memory as UTF-8    

    reader.readAsDataURL(fileToRead);
    // Handle errors load
    reader.onload = this.loadHandler;
    reader.onerror = this.errorHandler;
  } 

  
  loadHandler=(event)=>{
    const { values, handleChangefilebob } = this.props;
    var csv = event.target.result;
    console.log(csv);
    this.setState({downloadURL:csv})
    handleChangefilebob(csv);
    var storageRef = firebase.storage().ref("koinonia/comprobantes");

    var listRef = storageRef.child(this.props.values.files[0].name);
    listRef.putString(csv, 'base64url').then(function(snapshot) {
      console.log('Uploaded a blob or file!');
    });
    
  }

  errorHandler(evt) {
    if(evt.target.error.name == "NotReadableError") {
        alert("Canno't read file !");
    }
  }

  render() {
    const { values, subircap } = this.props;

    const files =values.files.map((file,index) => (
        <li key={file.name}>
          {file.name}
        </li>
      ));
    return (
        <section className="formg" ref={this.props.refe} >
            <h3 className="textokoiform">Comprobante de pago</h3>
         <Dropzone  className='dropzone'   onDrop={this.onDrop} >
        {({getRootProps, getInputProps}) => (
          <section className="container">
            <div {...getRootProps({className: 'dropzone'})}>
              <input {...getInputProps()} />
              <p>Arrastra un arrchivo el combrobante de deposito o haz click para seleccionarlo</p>
              <img
                  className="ref"
                  src={this.state.downloadURL || ""}
                  alt="Imagen"
                  height="200"
                  width="400"
                />
            </div>
            <aside>
              <h4>Archivo</h4>
              <ul>{files}</ul>
            </aside>
          </section>
        )}
      </Dropzone>
       
        <Button
          style={{
            background: "#1b0578",
            color: "#FFFFFF",
            marginRight: "1em",
            width: "40%",
            height:"10%"
          }}
          label="Back"
          onClick={this.back}
        >
          Atras
        </Button>
        <Button
          style={{background: "#fa7b25", color: "#ffff",
            width: "40%", height:"10%"
          }}
          label="Continue"
          onClick={subircap}
        >
          {" "}
          Finalizar
        </Button>
      </section>
    );
  }
}

export default FormPersonalDetails;
