import React, { Component, Fragment } from "react";
import {
  AppBar,
  Button,
  TextField,
  Toolbar,
  Typography
} from "@material-ui/core";
import Dropzone, {useDropzone} from 'react-dropzone';


export class FormPersonalDetails extends Component {
  continue = e => {
    e.preventDefault();
    this.props.nextStep();
  };
  back = e => {
    e.preventDefault();
    this.props.prevStep();
  };
  constructor() {
    super();
    this.onDrop = (files) => {
        this.getAsText(files[0]);
        this.setState({files})
        
    this.state = {
      disabled: false,
      files: []
    };
  }
  }
  
  getAsText=(fileToRead)=> {
    var reader = new FileReader();
    // Read file into memory as UTF-8      
    reader.readAsText(fileToRead);
    // Handle errors load
    reader.onload = this.loadHandler;
    reader.onerror = this.errorHandler;
  } 

  
  loadHandler=(event)=>{
    var csv = event.target.result;
    
    
      var faltantes = ''
      verificar.forEach(campo => {
        faltantes= faltantes+', '+campo
      })
     
      this.setState({files:[]})
      this.setState({alert:<CAlert color="danger">
      Este documento no tiene todos los campos requeridos.
      Campos faltantes: {faltantes}
      </CAlert> })

      setTimeout(() => {
        this.setState({alert:undefined})
      }, 25000);

        
    
  }

  errorHandler(evt) {
    if(evt.target.error.name == "NotReadableError") {
        alert("Canno't read file !");
    }
  }

  render() {
    const { values, handleChange } = this.props;
    return (
        <section className="formg" ref={this.props.refe} >
         <Dropzone  className='dropzone'  accept=".csv" onDrop={this.onDrop} >
        {({getRootProps, getInputProps}) => (
          <section className="container">
            <div {...getRootProps({className: 'dropzone'})}>
              <input {...getInputProps()} />
              <p>Arrastra un arrchivo csv o haz click para seleccionar</p>
            </div>
            <aside>
              <h4>Files</h4>
              <ul>{files}</ul>
            </aside>
          </section>
        )}
      </Dropzone>
       
        <Button
          style={{
            background: "#1b0578",
            color: "#FFFFFF",
            marginRight: "1em",
            width: "40%",
            height:"10%"
          }}
          label="Back"
          onClick={this.back}
        >
          Back
        </Button>
        <Button
          style={{background: "#fa7b25", color: "#ffff",
            width: "40%", height:"10%"
          }}
          label="Continue"
          onClick={this.continue}
        >
          {" "}
          Continue
        </Button>
      </section>
    );
  }
}

export default FormPersonalDetails;
