import React, { Component} from "react";
import {
  Button,
  TextField,

} from "@material-ui/core";

export class FormUserDetails extends Component {
  continue = e => {
    
    e.preventDefault();
    this.props.nextStep();
  };

  constructor() {
    super();
    this.state = {};
  }

  render() {
      
    const { values, handleChange } = this.props;
    return (
      <section className="formg"  >
        <h3 className="textokoiform">Datos</h3>
        <div style={{flex:"row"}}>
        <TextField
          label="Primer Nombre"
          onChange={handleChange("firstName")}
          defaultValue={values.firstName}
          variant="outlined" 
          helperText="Nombre"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 5,marginBottom:0,  width: "45%"}}
        />
        <TextField
          label="Apellido"
          onChange={handleChange("lastName")}
          defaultValue={values.lastName}
          variant="outlined" 
          helperText="Primer apellido"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 5,marginBottom:0,  width: "45%"}}
        />
        </div>
        <div className="selectdiv">
            <div className="select">
                    <select name="slct" id="slct" required  onChange={handleChange("tipo")}>
                      <option selected disabled>Tipo de Boleto</option>
                      <option value="1">Niño</option>
                      <option value="2">Adulto</option>
                    </select>
        
            </div>
        </div>
       

    <div style={{flex:"row"}}>
        <TextField
          label="Correo"
          onChange={handleChange("email")}
          defaultValue={values.email}
          variant="outlined" 
          helperText="Tu correo para enviarte información importante"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 5,marginBottom:0,  width: "45%"}}
        />
         <TextField
          label="Cedula"
          onChange={handleChange("cedula")}
          defaultValue={values.email}
          variant="outlined" 
          helperText="Cedula con guiones"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 5,marginBottom:0,  width: "45%"}}
        />
        </div>

        <div className="selectdiv" style={{display:"flex"}}>


          <div className="select">
            <select name="slct3" id="slct3" onChange={handleChange("red")}>
              <option selected disabled>Red Apostolica</option>
              
              <option value="Colón">Red juvenil</option>
              <option value="Colón">Colón</option>
                <option value="Panamá Norte">Panamá Norte </option>
                <option value="Panamá Este">Panamá Este</option>
                <option value="Panamá Centro">Panamá Centro</option>
                <option value="San Miguelito">San Miguelito</option>
                <option value="Circuito 8-8">Circuito 8-8</option>
                <option value="Pueblo Nuevo">Pueblo Nuevo</option>
              
            </select>
      
          </div>
          <div className="select">
              <select name="slct2" id="slct2" onChange={handleChange("igle")}>
                <option selected disabled>Iglesia hija</option>
                <option value="Colón">Colón</option>
                <option value="Penonomé chigore">Penonomé chigore </option>
                <option value="Penonomé los uveros">Penonomé los uveros</option>
                <option value="Barraza">Barraza</option>
                <option value="Hidekel">Hidekel</option>
                <option value="Arraijan">Arraijan</option>
                <option value="Chilibre">Chilibre</option>
                
               
              </select>
          
        </div>
          
        </div>

      
        <br />
        <br />
        <Button
          style={{ background: "#fa7b25", color: "#ffff",width: "90%",margin:"auto",height:"10%"}}
          label="Siguiente"
          onClick={this.continue}
        >
          Siguiente
        </Button>
      </section>
    );
  }
}

export default FormUserDetails;
