import React, { useEffect, useState ,useContext} from "react";
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import firebase from '../../firebase';
const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: 'yellow',
    color: 'black',
  },
  body: {
    fontSize: 12,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

function createData(nombre, cedula, igle, red, monto,tipo,url,uid) {
  return { nombre, cedula, igle, red, monto,tipo,url,uid };
}

const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
];


export class Preferences extends React.Component {
  constructor(props) {
    super(props);    
    this.state = {
      rows : []
    };
  }
  
  
 componentDidMount(){
  firebase.firestore().collection("koinonia-registros")
  .get()
  .then((querySnapshot) => {
   let newdata = this.state.rows
   
    querySnapshot.forEach((doc) => {
      var d = doc.data()
     newdata.push(createData(d.nombre +" "+d.apellido, d.cedula, d.igle, d.red, d.monto,d.tipo,d.url))
    
      });
      this.setState({rows:newdata})
    })

 }

  render() {


   return(
    <TableContainer component={Paper}>
      <Table aria-label="customized table" >
        <TableHead>
          <TableRow>
            <StyledTableCell align="left">Nombre</StyledTableCell>
            <StyledTableCell align="left">Cedula</StyledTableCell>
            <StyledTableCell align="left">Igelsia</StyledTableCell>
            <StyledTableCell align="left">Red</StyledTableCell>
            <StyledTableCell align="left">Monto</StyledTableCell>
            <StyledTableCell align="left">Boleto</StyledTableCell>
            <StyledTableCell align="right">...</StyledTableCell>
            <StyledTableCell align="right">Comprobante</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {this.state.rows.map((row) => (
            <StyledTableRow key={row.uid}>
              <StyledTableCell align="left">{row.nombre}</StyledTableCell>
              <StyledTableCell align="left">Cedula</StyledTableCell>
              <StyledTableCell align="left">Igelsia</StyledTableCell>
              <StyledTableCell align="left">Red</StyledTableCell>
              <StyledTableCell align="left">Monto</StyledTableCell>
              <StyledTableCell align="left">Boleto</StyledTableCell>
              <StyledTableCell align="right">...</StyledTableCell>
              <StyledTableCell align="right">Comprobante</StyledTableCell>
            </StyledTableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
   )
  }
}
export default Preferences;
