import React, { Component } from "react";
import Datospersonales from "./componentes/datospersonales";
import Captura from "./componentes/captura";
import Codigofinal from "./componentes/codidofinal";
import "react-step-progress-bar/styles.css";
import { ProgressBar, Step } from "react-step-progress-bar";
import lgmeeaa from "./componentes/logo_aposento nuevo-01.png"
export class UserForm extends Component {
  state = {
    step: 1,
    firstName: "",
    lastName: "",
    email: "",
    red: "",
    igle: "",
    tipo:"",
    cedula:"",
    id:"",
    files:[]
  };
  // Go to next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({
      step: step + 1
    });
  };

  // Go to prev step
  prevStep = () => {
    const { step } = this.state;
    this.setState({
      step: step - 1
    });
  };
  firstStep = () => {
    this.setState({
      step: 1
    });
  };
  // Handle fields change
  handleChange = input => e => {
    this.setState({ [input]: e.target.value });
    console.log(e.target.value )
  };
  handleChangefile = (input) =>  {
    this.setState({ files: input });
  };
  handleChangeid = (input) =>  {
    this.setState({ id: input });
  };

  renderSwitch(step) {
    const { firstName, lastName, email, igle, red ,files,tipo,cedula,id} = this.state;
    const values = { firstName, lastName, email, red, igle,files,tipo,cedula,id };

    switch (step) {
        default:
          return <h1>User Forms not working. Enable Javascript!</h1>;
        case 1:
          return (
            <Datospersonales
              nextStep={this.nextStep}
              handleChange={this.handleChange}
              values={values}
            />
          );
        case 2:
          return (
            <Captura
              nextStep={this.nextStep}
              prevStep={this.prevStep}
              handleChangefile={this.handleChangefile}
              handleChangeid={this.handleChangefile}
              values={values}
            />
          );
        case 3:
          return (
            <Codigofinal
              nextStep={this.nextStep}
              handleChange={this.handleChange}
              prevStep={this.prevStep}
              values={values}
            />
          );
        //
       
      }
  }

  render() {
    const { step } = this.state;
  

   return(
       <div className="formularioCont" ref={this.props.refProp}>
           <div className="barradeprogreso">
                <ProgressBar
                        percent={100/3*step}
                        filledBackground="linear-gradient(to right, #fefb72, #f0bb31)"
                    
                    >
                        <Step transition="scale">
                        {({ accomplished }) => (
                            <img
                            style={{ filter: `grayscale(${accomplished ? 0 : 80}%)` }}
                            width="30"
                            src={lgmeeaa}
                            />
                        )}
                        </Step>
                        <Step transition="scale">
                        {({ accomplished }) => (
                            <img
                            style={{ filter: `grayscale(${accomplished ? 0 : 80}%)` }}
                            width="30"
                            src={lgmeeaa}/>
                        )}
                        </Step>
                        <Step transition="scale">
                        {({ accomplished }) => (
                            <img
                            style={{ filter: `grayscale(${accomplished ? 0 : 80}%)` }}
                            width="30"
                            src={lgmeeaa}/>
                        )}
                        </Step>
                    </ProgressBar>

                    </div>
           {this.renderSwitch(step)}

       </div>
   )
  }
}
export default UserForm;
