import React, { Component, Fragment } from "react";
import {
  AppBar,
  Button,
  TextField,
  Toolbar,
  Typography,
  Select,
  MenuItem,
  InputLabel,
  FormControl

} from "@material-ui/core";

export class FormUserDetails extends Component {
  continue = e => {
    e.preventDefault();
    this.props.nextStep();
  };

  constructor() {
    super();
    this.state = {};
  }

  render() {
      
    const { values, handleChange } = this.props;
    return (
      <section className="formg"  >
        <h3 className="textokoiform">Datos</h3>
        <div style={{flex:"row"}}>
        <TextField
          label="Primer Nombre"
          onChange={handleChange("firstName")}
          defaultValue={values.firstName}
          variant="outlined" 
          helperText="Some important text"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 20,marginBottom:0,  width: "45%"}}
        />
        <TextField
          label="apellido"
          onChange={handleChange("lastName")}
          defaultValue={values.lastName}
          variant="outlined" 
          helperText="Some important text"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 20,marginBottom:0,  width: "45%"}}
        />
        </div>
        <FormControl variant="outlined" >
        <InputLabel htmlFor="outlined-age-native-simple">Niño o Adulto</InputLabel>
        <Select
          native
          value={values.tipo}
          onChange={handleChange}
          label="Niño o Adulto"
          inputProps={{
            name: 'age',
            id: 'outlined-age-native-simple',
          }}
          style={{ margin:"auto",  width: "200%"}}
        >
          <option aria-label="None" value="" />
          <option value={0}>Niño</option>
          <option value={1}>Adulto</option>
        </Select>
      </FormControl>
     

    <div style={{flex:"row"}}>
        <TextField
          label="Correo"
          onChange={handleChange("email")}
          defaultValue={values.email}
          variant="outlined" 
          helperText="Some important text"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 20,marginBottom:0,  width: "45%"}}
        />
         <TextField
          label="Cedula"
          onChange={handleChange("cedula")}
          defaultValue={values.email}
          variant="outlined" 
          helperText="Some important text"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 20,marginBottom:0,  width: "45%"}}
        />
        </div>

        <div style={{flex:"row"}}>
        <TextField
          label="Red Apostolica"
          onChange={handleChange("firstName")}
          defaultValue={values.firstName}
          variant="outlined" 
          helperText="Some important text"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 20,marginBottom:0,  width: "45%"}}
        />
        <TextField
          label="Iglesia hija"
          onChange={handleChange("lastName")}
          defaultValue={values.lastName}
          variant="outlined" 
          helperText="Some important text"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          style={{ marginLeft: 20,marginBottom:0,  width: "45%"}}
        />
        </div>
        <br />
        <br />
        <Button
          style={{ background: "#fa7b25", color: "#ffff",width: "90%",margin:"auto",height:"10%"}}
          label="Siguiente"
          onClick={this.continue}
        >
          Siguiente
        </Button>
      </section>
    );
  }
}

export default FormUserDetails;
