import React, { Component } from "react";
import Datospersonales from "./componentes/datospersonales";
import Captura from "./componentes/captura";


export class UserForm extends Component {
  state = {
    step: 1,
    firstName: "",
    lastName: "",
    email: "",
    occupation: "",
    city: "",
    bio: ""
  };
  // Go to next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({
      step: step + 1
    });
  };

  // Go to prev step
  prevStep = () => {
    const { step } = this.state;
    this.setState({
      step: step - 1
    });
  };
  firstStep = () => {
    this.setState({
      step: 1
    });
  };
  // Handle fields change
  handleChange = input => e => {
    this.setState({ [input]: e.target.value });
  };

  renderSwitch(step) {
    const { firstName, lastName, email, occupation, city, bio } = this.state;
    const values = { firstName, lastName, email, occupation, city, bio };

    switch (step) {
        default:
          return <h1>User Forms not working. Enable Javascript!</h1>;
        case 1:
          return (
            <Datospersonales
              refe
              nextStep={this.nextStep}
              handleChange={this.handleChange}
              values={values}
            />
          );
        case 2:
          return (
            <Captura
              nextStep={this.nextStep}
              prevStep={this.prevStep}
              handleChange={this.handleChange}
              values={values}
            />
          );
        case 3:
          return (
            <Datospersonales
              nextStep={this.nextStep}
              prevStep={this.prevStep}
              values={values}
            />
          );
        //
        case 4:
          return <Datospersonales firstStep={this.firstStep} />;
      }
  }

  render() {
    const { step } = this.state;
  

   return(
       <div className="formularioCont">
           {this.renderSwitch(step)}

       </div>
   )
  }
}
export default UserForm;
