import React, { Component } from "react";
import {
  Button,
} from "@material-ui/core";
import { QRCode } from 'react-qrcode-logo';
import Dropzone, {useDropzone} from 'react-dropzone';
import lgmeeaa from "./logo_aposento nuevo-01.png"

export class FormPersonalDetails extends Component {
  continue = e => {
    e.preventDefault();
    this.props.nextStep();
  };
  back = e => {
    e.preventDefault();
    this.props.prevStep();
  };
  constructor() {
    super();
    this.onDrop = (files) => {
        this.getAsText(files[0]);
        this.props.handleChangefile(files)
    }
    this.state = {
      disabled: false,
      files: []
    };
  }
  


  getAsText=(fileToRead)=> {
    var reader = new FileReader();
    // Read file into memory as UTF-8      
    reader.readAsText(fileToRead);
    // Handle errors load
    reader.onload = this.loadHandler;
    reader.onerror = this.errorHandler;
  } 

  
  loadHandler=(event)=>{
    var csv = event.target.result;
    


  }

  errorHandler(evt) {
    if(evt.target.error.name == "NotReadableError") {
        alert("Canno't read file !");
    }
  }

  render() {
    const { values, handleChange } = this.props;

    const files =values.files.map((file,index) => (
        <li key={file.name}>
          {file.name}
        </li>
      ));
    return (
        <section className="formg" ref={this.props.refe} >
            <h3 className="textokoiform">Guarda esta informaci칩n</h3>
            <div>
            <QRCode logoImage={lgmeeaa}  logoHeight={80} logoWidth={80} size={200} value={this.props.id} />
       
            </div>

            <div>
            <h4 className="textokoiform">Guarda esta informaci칩n</h4>
            <h4 className="textokoiform">Nombre :{this.props.firstName+" " +this.props.lastName}</h4>
            <h4 className="textokoiform">Boleto de : {this.props.tipo == 1 ? 'ni침o':'Adulto'}</h4>
            </div>
            
        
        
      </section>
    );
  }
}

export default FormPersonalDetails;
